import React from 'react';
import axios from "axios";
import validate from './Validation'; 
import Submit from './Submit';
import './App.css';

const baseURL = "https://localhost:3000";

const Form = () => {
  const {
    values,
    errors,
    handleChange,
    handleSubmit,
  } = Submit(getMedianLimit, validate);

  function getMedianLimit() { 
        axios
        .post(baseURL, {
            limit: "Hello World!", 
            })
        .then((response) => {
        setPost(response.data);
        });
  }

  return (
    <form onSubmit={handleSubmit} noValidate>
    <div id="main">
      <label className="label">Enter a numerical value</label>
      <div className="control">
        <input autoComplete="off" className={`input ${errors.limit && 'is-danger'}`} type="email" name="email" onChange={handleChange} value={values.email || ''} required />
        {
            errors.limit && (
             <p className="help danger">{errors.limit}</p>
            )
        }
      </div>
      <button type="submit" className="btn btn-primary">Submit</button>
    </div>
    </form>
  )
}


export default Form;
