import React from 'react';
import validate from './Validation'; 
import useForm from './useForm';
import './App.css';
import { response } from 'express';


const Form = () => {
  const {
    values,
    errors,
    handleChange,
    handleSubmit,
  } = useForm(getMedianLimit, validate);


  function getMedianLimit() { 
     
  }

  return (
    <><form onSubmit={handleSubmit} noValidate>
          <div id="main">
              <label className="label">Enter a numerical value</label>
              <div className="control">
                  <input
                      autoComplete="off" className={`input ${errors.limit && 'is-danger'}`}
                      aria-invalid={errors.limit ? "true" : "false"}
                      name="limit" onChange={handleChange} value={values.limit || ''} required />
                  {errors.limit && (
                      <p className="help danger">{errors.limit}</p>
                  )}
              </div>
              <button type="submit" className="btn btn-primary" value="Submit">Submit</button>
          </div>
      </form><div>
              { {isSuccess} && (
                  <p className="help danger">{errors.limit}</p>
              )}
          </div></>
  )
}


export default Form;
