export default function validate(values) {
    let errors = {}; 
    if (!values.limit) {
        errors.limit = 'A number is required.';
    }
    else if ((/^ [0-9]+$/).test(values.limit)) {
        errors.limit = "Please enter a numerical value."
    }
    

    return errors;
};
